import React, { useState } from "react";
import { Dropdown } from 'primereact/dropdown';

const menuItems = [
    {
      name: 'Ofertas',
    },
    {
      name: 'Mis compras',
    },
];

   

const NavBar = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const categories = [
        { name: 'Categoría 1', value: 1 },
        { name: 'Categoría 2', value: 2 }, 
        { name: 'Categoría 3', value: 3 }, 
    ]; 

    return (
        <div>
           <ul className="flex flex-row justify-around p-4 w-full">
               <li>
                 <Dropdown value={selectedCategory} onChange={(e) => setSelectedCategory(e.value)} options={categories} 
                optionLabel="name" placeholder="Categorias" className="w-full md:w-14rem" />
                </li> 
              {menuItems.map(item => (
                <li>
                  <a>{item.name}</a>
                </li>
              ))}
            </ul> 
        </div>
    );
}

export default NavBar;
