import React, {useState} from 'react';
import { Dropdown } from 'primereact/dropdown';

const menuItems = [
    {
      name: 'Ofertas',
    },
    {
      name: 'Mis compras',
    },
];

const [selectedCategory, setSelectedCategory] = useState(null);
const categories = [
    { name: 'Categoría 1', value: 1 },
    { name: 'Categoría 2', value: 2 }, 
    { name: 'Categoría 3', value: 3 }, 
];    

const NavBar = () => {
    return (
        <div className="flex flex-row justify-around p-4 w-full">
            <div className="card flex justify-content-center">
                <Dropdown value={selectedCategory} onChange={(e) => setSelectedCategory(e.value)} options={categories} 
                optionLabel="name" placeholder="Categoriad" className="w-full md:w-14rem" />
            </div>
           <ul className="flex flex-row justify-around p-4 w-full">
              {menuItems.map(item => (
                <li>
                  <a>{item.name}</a>
                </li>
              ))}
            </ul> 
        </div>
    );
}

export default NavBar;
